import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import '../styles/searchButton.css';
import { CloseButton } from './closeButton';



function SearchField(props) {
const [isOpen, setIsOpen] = useState(false);

const handleSearchClick = () => {
setIsOpen(true);
}

const handleCloseClick = () => {
setIsOpen(false);
}

return (
<div>
    <CloseButton style={{ display: isOpen ? 'block' : 'none' }} onClick={handleCloseClick} />
    <button className={`search ${isOpen ? 'open' : ''}`} onClick={handleSearchClick} >
        <FontAwesomeIcon size="2x" icon={faSearch}  className="searchIcon" style={{display: isOpen ? 'none' : ''}}/>
        <input type="text" className="input" style={{ display: isOpen ? 'block' : 'none' }} />
    </button>



</div>
);
}

export default SearchField;
