import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import '../styles/searchButton.css';
import { CloseButton } from './closeButton';



function SearchField() {
const [isOpen, setIsOpen] = useState(false);

const handleSearchClick = () => {
setIsOpen(true);
}

const handleCloseClick = () => {
setIsOpen(false);
}

return (
<div className={`search ${isOpen ? 'open' : ''}`}>
<FontAwesomeIcon icon={faSearch} onClick={handleSearchClick} />
<input type="text" className="input" style={{ display: isOpen ? 'block' : 'none' }} />
<CloseButton style={{ display: isOpen ? 'block' : 'none' }} onClick={handleCloseClick} />
</div>
);
}

export default SearchField;
